const fs = require('fs');
const path = require('path');

function calcularFactorial(n) {
    if (n === 0 || n === 1) return 1;
    return n * calcularFactorial(n - 1);
}

function leerArchivosDeCarpeta(carpeta) {
    const archivos = fs.readdirSync(carpeta);
    return archivos.map(archivo => {
        const contenido = fs.readFileSync(path.join(carpeta, archivo), 'utf-8');
        return {
            nombre: archivo,
            contenido
        };
    });
}

function imprimirInfoSistema() {
    const os = require('os');
    console.log('Informaci√≥n del Sistema:');
    console.log(`Plataforma: ${os.platform()}`);
    console.log(`Arquitectura: ${os.arch()}`);
    console.log(`Cores: ${os.cpus().length}`);
    console.log(`Memoria Libre: ${os.freemem()}`);
}

function ejecutarTodo() {
    console.log('Calculando Factorial de 5: ', calcularFactorial(5));
    console.log('Leyendo Archivos de la Carpeta "peksi_data": ', leerArchivosDeCarpeta(path.join(__dirname, 'peksi_data')));
    imprimirInfoSistema();
}

ejecutarTodo();

module.exports = {
    calcularFactorial,
    leerArchivosDeCarpeta,
    imprimirInfoSistema,
    ejecutarTodo
};
